<script>

const Requisites = () => import('@/components/Payment/Requisites.vue');

export default {
  name: "Requisites",
  components: {Requisites},
  methods: {
    async copyText() {
      if (navigator.clipboard && window.isSecureContext) {
        await navigator.clipboard.writeText(this.number.replace(/-|\s/g,""));
      } else {
        const textArea = document.createElement("textarea");
        textArea.value = this.number.replace(/-|\s/g,"");
        textArea.style.position = "absolute";
        textArea.style.left = "-999999px";
        document.body.prepend(textArea);
        textArea.select();

        try {
          document.execCommand('copy');
        } catch (error) {
          console.error(error);
        } finally {
          textArea.remove();
        }
      }
    }
  },
  props: {
    number: {
      type: String,
      required: false,
      default: null
    },

    bank: {
      type: String,
      required: false,
      default: ''
    },

    name: {
      type: String,
      required: false,
      default: null
    },
    issuer: {
      type: String,
      required: false,
      default: null
    },

    reqimage: {
      type: String,
      required: false,
      default: null
    },

    amount: {
      type: Number,
      required: false,
      default: null
    },

    currency: {
      type: String,
      required: false,
      default: null
    }
  }
}
</script>

